  const [showBadge, setShowBadge] = useState(false);
  const handleShowHideBadge = () => {
    setShowBadge(!showBadge);
  };
  return (
    <div style={{ padding: "1rem" }}>
      <IcButton variant="secondary" onClick={handleShowHideBadge}>
        <IcBadge
          variant="info"
          textLabel="8"
          slot="badge"
          visible={showBadge}
        />
        Coffee orders
      </IcButton>
    </div>
  );
};

