    if (newValue && newValue.length > 1) {
      const mockAPI = (query) => {
        const filteredResults = mockData.filter((item) => 
          item.label.toLowerCase().includes(query.toLowerCase()));
        return new Promise(resolve => { 
          setTimeout(() => resolve(filteredResults), 1000)
